async function sendWebhook(url, content, embed) {
  var requestOne = new XMLHttpRequest();
  requestOne.open("POST", `${url}`);
  requestOne.setRequestHeader('Content-type', 'application/json');
  if (embed) {
    var paramsOne = {
      username: "Blooket Bot",
      embeds: [ content ]
    }
  } else {
    var paramsOne = {
      username: "Blooket Bot",
      content: `${content}`
    }
  }
  requestOne.send(JSON.stringify(paramsOne));
}

